Post._content:
 Unity  UI  Mask shader

 Object  RawImage  Material  shader (0.5, 0.5)  alpha  0 
 `(0, 0, 0, 0)` fragment shader (shader `frag`)

 shader:

<!--more-->

{% codeblock %}
Shader "Sprites/Circle"
{
    Properties
    {
    [PerRendererData] _MainTex ("Sprite Texture", 2D) = "white" {}
    _Color ("Tint", Color) = (1,1,1,1)
    _Center("Center", vector) = (0.5, 0.5, 1.0, 1.0)
    _RadiusScale("Radius Scale", Range(0, 1)) = 0.5
    _HorizontalScale("Horizontal Scale", Range(0, 1.0)) = 1.0
    _VerticalScale("Vertical Scale", Range(0, 1.0)) = 1.0
    }

    SubShader
    {
    Tags
    {
        "Queue"="Overlay"
        "IgnoreProjector"="True"
        "RenderType"="Transparent"
        "PreviewType"="Plane"
        "CanUseSpriteAtlas"="True"
    }

    Cull Off
    Lighting Off
    ZWrite Off
    Blend One OneMinusSrcAlpha

    Pass
    {
    CGPROGRAM
        #pragma vertex vert
        #pragma fragment frag
        #pragma multi_compile _ PIXELSNAP_ON
        #include "UnityCG.cginc"

        struct appdata_t
        {
        float4 vertex   : POSITION;
        float4 color    : COLOR;
        float2 texcoord : TEXCOORD0;
        };

        struct v2f
        {
        float4 vertex   : SV_POSITION;
        fixed4 color    : COLOR;
        half2 texcoord  : TEXCOORD0;
        };

        fixed4 _Color;
        float _RadiusScale;
        float4 _Center;
        float _HorizontalScale;
        float _VerticalScale;

        v2f vert(appdata_t IN)
        {
        v2f OUT;
        OUT.vertex = mul(UNITY_MATRIX_MVP, IN.vertex);
        OUT.texcoord = IN.texcoord;
        OUT.color = IN.color * _Color;
        #ifdef PIXELSNAP_ON
        OUT.vertex = UnityPixelSnap (OUT.vertex);
        #endif

        return OUT;
        }

        sampler2D _MainTex;

        fixed4 frag(v2f IN) : SV_Target
        {
        fixed4 c;
        c = tex2D(_MainTex, IN.texcoord) * IN.color;

        // adjust center and horizontal/verital scale
        //
        float2 scale = (_HorizontalScale, _VerticalScale);
        float rs = length((_Center.xy - IN.texcoord.xy) / scale.xy);

        if(rs < 0.5 * _RadiusScale)
        {

            c.rgb *= c.a;
        }
        else
        {
            c = (0, 0, 0, 0);
        }

        return c;
        }
    ENDCG
    }
   }
}
{% endcodeblock %}

Github  <https://raw.githubusercontent.com/wudixiaop/Nana/master/Shader/Sprite-Circle.shader>

